mapper.select:
SELECT *, CONTRACT_AMOUNT - PAID_AMOUNT AS REMAININGAMOUNT FROM OUT_CONTRACT_INFO LEFT JOIN PROCESS_SUPPLIER_INFO ON PROCESS_SUPPLIER_INFO.CONTRACT_ID = OUT_CONTRACT_INFO.CONTRACT_ID LEFT JOIN MATTER_INFO ON MATTER_INFO.CONTRACT_ID = OUT_CONTRACT_INFO.CONTRACT_ID WHERE OUT_CONTRACT_INFO.STATE = 0
AND ${whereSql}
UNION ALL SELECT *, CONTRACT_AMOUNT - PAID_AMOUNT AS REMAININGAMOUNT FROM OUT_CONTRACT_INFO LEFT JOIN PROCESS_SUPPLIER_INFO ON PROCESS_SUPPLIER_INFO.CONTRACT_ID = OUT_CONTRACT_INFO.CONTRACT_ID AND PROCESS_SUPPLIER_INFO.CONTRACTLOG_ID = OUT_CONTRACT_INFO.CONTRACTLOG_ID LEFT JOIN MATTER_INFO ON MATTER_INFO.CONTRACT_ID = OUT_CONTRACT_INFO.CONTRACT_ID AND MATTER_INFO.CONTRACTLOG_ID = OUT_CONTRACT_INFO.CONTRACTLOG_ID WHERE OUT_CONTRACT_INFO.STATE = 1
AND ${whereSql}

SELECT OUT_CONTRACT_INFO.CONTRACT_ID,OUT_CONTRACT_INFO.PROJECT_NAME,OUT_CONTRACT_INFO.APPROVAL_ID,
OUT_CONTRACT_INFO.SUPPLIER_CODE,MATTER_INFO.MATTER_COUNT
FROM MATTER_INFO LEFT JOIN OUT_CONTRACT_INFO ON MATTER_INFO.CONTRACT_ID = OUT_CONTRACT_INFO.CONTRACT_ID
WHERE OUT_CONTRACT_INFO.CONTRACT_ID=#{contractId} AND MATTER_INFO.MATTER_CODE=#{materialCode}
AND OUT_CONTRACT_INFO.BUY_SOURCE=#{buySource}

SELECT OUT_CONTRACT_INFO.CONTRACT_ID,OUT_CONTRACT_INFO.PROJECT_NAME,OUT_CONTRACT_INFO.APPROVAL_ID,
OUT_CONTRACT_INFO.SUPPLIER_CODE,MATTER_INFO.MATTER_COUNT
FROM MATTER_INFO LEFT JOIN OUT_CONTRACT_INFO ON MATTER_INFO.CONTRACT_ID = OUT_CONTRACT_INFO.CONTRACT_ID
WHERE OUT_CONTRACT_INFO.CONTRACT_ID=#{contractId}
AND MATTER_INFO.CONTRACTLOG_ID = OUT_CONTRACT_INFO.CONTRACTLOG_ID
AND MATTER_INFO.MATTER_CODE=#{materialCode}
AND OUT_CONTRACT_INFO.BUY_SOURCE=#{buySource}
